
public class Ex02 {

	public static void main(String[] args) {
		Boolean loop = true;
		while(loop) {
			for(int i=1;i<=5;i++) {
				System.out.println("임지훈은 쾌남 중의 쾌남 입니다");
				if(i==5) {
					loop = false;
					System.out.println("이제 임지훈 님은 더이상 쾌남이 아닙니다.");
				}
			}
			
			
			
			
			
		}
		// TODO Auto-generated method stub

	}

}
